import React from 'react';
import './search.css';

class Books extends React.Component {
    
    state = {
        header: <thead></thead>,
        books: [],
        name: ''
    };

    fetchData = () => {
        var id = document.getElementById('id').value;
        this.setState({header: <thead id="header">
                        <tr>
                            <th scope="col">Book Name</th>
                            <th scope="col">Author</th>
                            <th scope="col">Introduction</th>
			    <th scope="col">Image</th>
			    <th scope="col"></th>
                        </tr>
                    </thead>,
            books: []});

        fetch(`/api/employees/${id}`)
            .then(res => res.json())
            .then(student => {
                if(student.length > 0) {
                    this.setState({...this.state, name: `Following Employees Issued This Book`});
                    student.forEach(
                        (el) => {
				if (el.count > 0){
					this.setState({
						books: [...this.state.books, 
                                    		<tr key={el.id}>
                                        	<td>{el.name}</td>
	                                        <td>{el.author}</td>
        	                                <td>{el.introduction}</td>
						<td><img src={el.image} alt="No" />=</td>
						<td><button>Issue</button></td>
                	                    </tr>]
					})
				}else{
					this.setState({
						books: [...this.state.books, 
	                                	<tr key={el.uid}>
        	                                <td>{el.uid}</td>
                	                        <td>{el.date}</td>
                        	                <td>{el.deadline}</td>
                                	    </tr>]
                                        })
				}
			})
                }
                else{
                    this.setState({header: []});
                    this.setState({...this.state, name: "No One Have Issued This Book"});
                }
                });
    }

    returnIt = el => {
        fetch('/api/return', {
                method: 'post',
                headers: {'Content-Type':'application/json'},
                body: JSON.stringify({
                    ...el,
                    sid: parseInt(document.getElementById('sid').value)
                })
           });

    }

    render() {
        return (
            <div id='search' className="text-center">
                <div>
                    <input className="form-control sel"  placeholder="Enter Book Name" id="id" min="1"></input>
                    <button className="btn btn-success" onClick={this.fetchData}>Submit</button>
                </div><br/>
                {this.state.name}
                <table id="sResults" className="table table-hover">
                    {this.state.header}
                    <tbody>
                        {this.state.books}
                    </tbody>
                </table>
            </div>
        );
    }

}

export default Books;
